package advent_2022.puzzles

fun day6() {
    println("December 6th (Part 1) : ${findMarkerPosition(input, 4)}")
    println("December 6th (Part 2) : ${findMarkerPosition(input, 14)}")
}

private fun findMarkerPosition(input: String, packetLength: Int) : Int {
    var pattern = ""

    input.forEachIndexed { index, char ->
        if(pattern.length < packetLength) {
            pattern += char
        } else if(pattern.toCharArray().distinct().size < packetLength) {
            pattern = (pattern.substring(1) + char)
        } else {
            return index
        }
    }

    return -1
}

